package day1;

public class Exercise4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 100;
		int result;
		
		result = number + 10;
		System.out.printf("덧셈 연산의 결과 : %d", result);
		System.out.print('\n');
		
		result = number - 10;
		System.out.printf("뺄셈 연산의 결과 : %d", result);
		System.out.print('\n');
		
		result = number * 10;
		System.out.printf("곱셈 연산의 결과 : %d", result);
		System.out.print('\n');
		
		result = number / 10;
		System.out.printf("나눗셈 연산의 결과 : %d", result);
		System.out.print('\n');
		
	}

}
